import {
  CREATE_CATEGORY,
  UPDATE_CATEGORY,
  DELETE_CATEGORY,
  GETALL_CATEGORY,
} from "../actions/categoryActions";

const initialState = {
  categories: [],
  currentCategory: {},
}; 

export const categoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case GETALL_CATEGORY:
      return {
        ...state,
        categories: [...action.payload],
      };
    // case CREATE_CATEGORY:
    //   let newCategory = {
    //     id: action.payload.categoryId,
    //     name: action.payload.categoryName,
    //     image: action.payload.categoryImage,
    //   };
    //   return {
    //     ...state,
    //     categories: [...state.categories, newCategory],
    //     currentCategory: newCategory,
    //   };
    case CREATE_CATEGORY:
      console.log("CREATE_CATEGORY", action.payload);
      return {
        ...state,
        categories: [...state.categories, action.payload],
      };
    case UPDATE_CATEGORY:
      return {
        ...state,
        categories: state.categories.map((x) => {
          if (x.id === action.payload.categoryId) {
            return {
              ...x,
              name: action.payload.categoryName,
              image: action.payload.categoryImage,
            };
          }
          return x;
        }),
      };
    case DELETE_CATEGORY:
      return {
        ...state,
        categories: state.categories.filter(
          (x) => x.id !== action.payload.categoryId
        ),
      };
    default:
      return { ...state };
  }
};
