import React from "react";
import { Text, View, StyleSheet, TouchableOpacity } from "react-native";
//import Ionicons from "react-native-vector-icons/Ionicons";
import { Ionicons } from "@expo/vector-icons";
//step1
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const ViewAllCategory = ({ navigation }) => {
  const db = useSelector((state) => state.tags);
  console.log(db);
  return (
    <View style={styles.CRUDcontainer}>
      <View style={styles.tagAllContainer}>
        {db.tags ? (
          db.tags.map((tag) => {
            return (
              <TouchableOpacity
                key={tag.id}
                style={styles.tagContainer}
                onPress={() =>
                  navigation.navigate("Edit", {
                    tagId: tag.id,
                    tagName: tag.name,
                    tagIcon: tag.icon,
                  })
                }
              >
                <View style={styles.tagIconBtn}>
                  <Ionicons
                    style={styles.notiBtn}
                    name={tag.icon}
                    color="#000"
                    size={18}
                  />
                  <Text style={styles.tagText}> {tag.name}</Text>
                </View>
              </TouchableOpacity>
            );
          })
        ) : (
          <View>
            <Text>No data</Text>
          </View>
        )}
      </View>

      <TouchableOpacity
        style={styles.btn}
        onPress={() => navigation.navigate("Create")}
      >
        <Text style={styles.btnText}>Create New</Text>
      </TouchableOpacity>
    </View>
  );
};
export default ViewAllCategory;
const styles = StyleSheet.create({
  CRUDcontainer: {
    flex: 1,
  },
  tagContainer: {
    alignItems: "center",
    justifyContent: "center",
    height: 45,
    borderRadius: 45 / 2,
    borderWidth: 1,
    padding: 10,
    borderColor: "#FF7867",
    margin: 10,
  },
  tagText: {
    color: "#17171C",
  },
  tagAllContainer: {
    flexDirection: "row",
    flexWrap: "wrap",
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
    alignSelf: "center",
  },
  btnText: {
    color: "#FFF",
  },
  tagIconBtn: {
    flexDirection: "row",
  },
});
