import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
  TextInput,
  Image,
  ScrollView,
  Modal,
  Pressable,
} from "react-native";

import { Ionicons, AntDesign } from "@expo/vector-icons";
import { fetchAllPet } from "../redux/actions/petActions";
//step1
import { useDispatch, useSelector } from "react-redux";
import ReadMore from "@fawazahmed/react-native-read-more";

const ViewAllCategory = ({ navigation }) => {
  const dispatch = useDispatch();
  const [ModalVisible, setModalVisible] = useState(false);
  const db = useSelector((store) => store.pets);
  const [data, setData] = useState([]);
  useEffect(() => {
    dispatch(fetchAllPet());
    setData(db.pets);
    //console.log("Hello", data);
  }, []);
  return (
    <View style={styles.CRUDContainer}>
      <View>
        <Text style={styles.tiltle}>LIST PET</Text>
      </View>
      <View style={styles.ProductAllContainer}>
        {db.pets ? (
          db.pets.map((item, index) => {
            return (
              <View key={index} style={styles.tagContainer}>
                <View style={styles.contentMain}>
                  <Image source={item.Image} style={styles.img} />
                  <View style={styles.View}>
                    <Text style={styles.textName}>Tên: {item.Name}</Text>
                    <Text style={styles.textPrice}>Giá: {item.Price} VNĐ</Text>
                    <View>
                      <TouchableOpacity
                        style={styles.btnUpdate}
                        onPress={() =>
                          navigation.navigate("EditPet", {
                            petId: item.doc(docId),
                            petCategory: item.Cate,
                            petName: item.Name,
                            petAge: item.Age,
                            petGender: item.Gender,
                            petPrice: item.Price,
                            petCondition: item.Condition,
                            petImage: item.Image,
                            petOrigin: item.Origin,
                          })
                        }
                      >
                        <AntDesign name="edit" size={24} color="black" />
                        <Text style={styles.txtUpdate}>Update</Text>
                      </TouchableOpacity>
                    </View>
                    <View style={{ marginTop: 10 }}>
                      <TouchableOpacity
                        style={styles.btnView}
                        onPress={() => {
                          setModalVisible(true);
                        }}
                      >
                        <Ionicons name="search" size={24} color="black" />
                        <Text style={styles.txtView}>View Detail</Text>
                      </TouchableOpacity>
                    </View>
                    <View style={styles.centeredView}>
                      <Modal
                        animationType="slide"
                        transparent={true}
                        visible={ModalVisible}
                        onRequestClose={() => setModalVisible(false)}
                      >
                        <Pressable
                          style={styles.outsideModal}
                          onPress={(event) => {
                            if (event.target == event.currentTarget) {
                              setModalVisible(false);
                            }
                          }}
                        >
                          <View style={styles.modal}>
                            <View style={styles.imageContainer}>
                              <View style={styles.image}>
                                <Image
                                  style={styles.imgInfo}
                                  source={item.Image}
                                />
                              </View>
                              <View style={styles.text}>
                                <Text style={styles.txtName}>{item.Name}</Text>
                                <Text style={styles.txtPrice}>
                                  {item.Price} VNĐ
                                </Text>

                                <TouchableOpacity
                                  title="Add to cart"
                                  onPress={() => navigation.navigate("Cart")}
                                ></TouchableOpacity>
                              </View>
                            </View>
                            <Text style={styles.text4}>Thông tin chi tiết</Text>
                            <View style={styles.description}>
                              <View>
                                <Text style={styles.textTitle}>ID: </Text>
                                <Text style={styles.textTitle}>Category:</Text>
                                <Text style={styles.textTitle}>Age:</Text>
                                <Text style={styles.textTitle}>Gender:</Text>
                                <Text style={styles.textTitle}>Condition:</Text>
                                <Text style={styles.textTitle}>Origin:</Text>
                              </View>
                              <View>
                                <Text style={styles.text3}>{item.doc(docId)}</Text>
                                <Text style={styles.text3}>
                                  {item.Cate}
                                </Text>
                                <Text style={styles.text3}>{item.Age}</Text>
                                <Text style={styles.text3}>{item.Gender}</Text>
                                <Text style={styles.text3}>
                                  {item.Condition}
                                </Text>
                                <Text style={styles.text3}>{item.Origin}</Text>
                              </View>
                            </View>
                            {/* <Text style={styles.textTitle}>Mô tả:</Text>
                            <ReadMore numberOfLines={3} style={styles.textDes}>
                              Không chỉ có một vẻ ngoài đáng yêu và ưa nhìn,
                              những chú mèo Munchkin cũng có tính cách rất dễ
                              chịu và thân thiện. Những chú mèo Munchkin rất
                              tinh nghịch, lém lỉnh và vui tươi như vẻ ngoài của
                              chúng. Tuy nhiên, chúng cũng rất ngọt ngào, đặc
                              biệt là rất quấn chủ. Ngoài ra, những chú mèo
                              Munchkin cũng rất thông minh, hiền lành và dễ
                              tính. Chúng rất hòa đồng và dễ gần với trẻ con.
                              Những gia đình có trẻ nhỏ thì nên nuôi một chú mèo
                              Munchkin trong nhà để trẻ nhỏ được bầu bạn với thú
                              cưng
                            </ReadMore> */}
                            <TouchableOpacity
                              style={styles.btnClosingModal}
                              onPress={() => setModalVisible(false)}
                            >
                              <Text style={styles.btnText}>Back</Text>
                            </TouchableOpacity>
                          </View>
                        </Pressable>
                      </Modal>
                    </View>
                  </View>
                </View>
              </View>
            );
          })
        ) : (
          <View>
            <Text>No data</Text>
          </View>
        )}
      </View>
      <View style={{ alignItems: "center", justifyContent: "center" }}>
        <TouchableOpacity
          style={styles.btn}
          onPress={() => {
            navigation.navigate("CreatePet");
          }}
        >
          <Ionicons style={styles.notiBtn} name="add" color="#fff" size={24} />
        </TouchableOpacity>
      </View>
    </View>
  );
};
export default ViewAllCategory;
const styles = StyleSheet.create({
  CRUDContainer: {
    flex: 1,
    alignItems: "center",
    marginTop: 4,
  },
  bgContainer: {
    height: "auto",
  },
  contentMain: {
    flexDirection: "row",
  },
  View: {
    marginLeft: 24,
    marginBottom: 28,
    justifyContent: "center",
  },
  ProductAllContainer: {
    width: "100%",
    //flexDirection: "row",
    // alignItems: "center",
    //flexWrap: "wrap",
  },
  tagContainer: {
    width: 340,
    height: 160,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#d81b60",
    paddingLeft: 10,
    margin: 10,
  },
  tagCT: {
    width: "auto",
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    padding: 10,
    margin: 10,
    borderRadius: 25,
    borderWidth: 1,
    borderColor: "#fff",
    backgroundColor: "#fff",
  },
  textName: {
    color: "#d81b60",
    fontSize: 25,
    marginTop: 8,
    marginLeft: -4,
  },
  textPrice: {
    color: "#d81b60",
    fontSize: 20,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: 45,
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
  },
  img: {
    width: 120,
    height: 120,
    borderRadius: 20,
    marginTop: 16,
  },
  tiltle: {
    paddingTop: 20,
    fontSize: 30,
    fontWeight: "bold",
    color: "red",
  },
  tagss: {
    fontSize: 10,
    color: "#000",
  },
  tagAllContainer: {
    flexDirection: "row",
    alignSelf: "flex-start",
  },
  btnUpdate: {
    backgroundColor: "#ef9a9a",
    borderRadius: 5,
    flexDirection: "row",
    width: 100,
  },
  btnView: {
    backgroundColor: "#b39ddb",
    borderRadius: 5,
    flexDirection: "row",
    width: 128,
  },
  txtUpdate: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
  txtView: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
  ////////////////////////////////////////////////////////////////
  modal: {
    width: "90%",
    height: 600,
    backgroundColor: "#ede7f6",
    borderRadius: 10,
    marginLeft: 18,
    marginTop: 80,
    //alignItems: "center",
    //justifyContent: "center",

    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,

    elevation: 24,
  },
  imgInfo: {
    height: 180,
    width: 150,
    marginLeft: 5,
    marginTop: 5,
    borderRadius: 18,
  },
  imageContainer: {
    flexDirection: "row",
  },
  text: {
    width: "55%",
    padding: 5,
    marginTop: 25,
  },
  txtPrice: {
    fontSize: 20,
    color: "red",
  },
  txtName: {
    color: "#d81b60",
    fontSize: 25,
  },
  textDes: {
    marginLeft: 6,
  },
  text3: {
    fontSize: 15,
    marginLeft: 10,
    flexWrap: "wrap",
  },
  textTitle: {
    fontSize: 15,
    color: "black",
    fontWeight: "bold",
    marginLeft: 6,
  },
  text4: {
    fontSize: 20,
    fontWeight: "bold",
    textAlign: "center",
  },
  btnClosingModal: {
    marginTop: 8,
    alignSelf: "center",
    backgroundColor: "#f06292",
    width: 100,
    alignItems: "center",
    borderRadius: 20,
  },
  btnText: {
    fontSize: 30,
    color: "#f3e5f5",
  },
  description: {
    flexDirection: "row",
  },
});
