import React, { useState, useEffect } from "react";
import {
  FlatList,
  StyleSheet,
  Text,
  View,
  Image,
  // Dimensions,
  ImageBackground,
  TouchableOpacity,
  TextInput,
  Modal,
  Pressable,
} from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllCategory, SearchCate } from "../redux/actions/categoryActions";
import { Ionicons, AntDesign } from "@expo/vector-icons";

const ViewAllCategory = ({ navigation, route }) => {
  const [ModalVisible, setModalVisible] = useState(false);
  const db = useSelector((store) => store.categories);
  const dispatch = useDispatch();
  // const { productId } = route.params;
  // const [docId, setdocId] = useState(productId);
  console.log(db);
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const handleSearchCate = (key) => {
    if (key.length > 0) {
      dispatch(SearchCate(key));
    } else {
      dispatch(fetchAllCategory());
    }
  };
  useEffect(() => {
    dispatch(fetchAllCategory());
    // setData(db.courses)
    console.log(data);
  }, []);

  const ItemComponent = ({ item }) => {
    return (
      <View key={item.docId} style={styles.tagContainer}>
        <View style={styles.contentMain}>
          <Image source={{ uri: item.Img }} style={styles.img} />
          <View style={styles.View}>
            <Text style={styles.tagText}>{item.name}</Text>
            <View style={{ marginTop: 10 }}>
              <TouchableOpacity
                style={styles.btnView}
                onPress={() =>
                  navigation.navigate("ViewAll", {
                    cateId: item.id,
                    cateName: item.name,
                    cateImg: item.Img,
                  })
                }
              >
                <Ionicons name="search" size={24} color="black" />
                <Text style={styles.txtView}>View List</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </View>
    );
  };
  return (
    <View style={styles.ListItemContainer}>
      <ImageBackground
        style={styles.background}
        resizeMode="cover"
        source={{
          uri: "https://i.pinimg.com/originals/23/e7/6b/23e76b9437ea6d9ed105dce1bc4d061c.jpg",
        }}
      >
        <View style={styles.leftHeader}>
          <TouchableOpacity
            style={styles.avatar}
            onPress={() => {
              navigation.openDrawer();
            }}
          >
            <View style={styles.btnicon}>
              <Ionicons
                name="apps-outline"
                color="#fff"
                size={19}
                style={{
                  justifyContent: "center",
                  alignSelf: "center",
                }}
              />
            </View>
          </TouchableOpacity>
          <TextInput
            placeholder="Search"
            style={styles.inputText}
            onChangeText={(text) => setSearch(text)}
          />
          <TouchableOpacity
            onPress={() => handleSearchCate(search)}
            style={styles.btnSearch}
          >
            <Text style={styles.search}>Tìm kiếm</Text>
          </TouchableOpacity>
        </View>
        <Text style={styles.headerComponentTitle}>WELCOME TO SHOP PET</Text>

        <FlatList
          data={db.categories}
          //ListHeaderComponent={HeaderComponent}
          numColumns={2}
          renderItem={ItemComponent}
        ></FlatList>
      </ImageBackground>
    </View>
  );
};

export default ViewAllCategory;
