export const CREATE_PET = "CREATE_PET";
export const UPDATE_PET = "UPDATE_PET";
export const DELETE_PET = "DELETE_PET";

//action creator
export const createPet = (pet) => {
  return {
    type: "CREATE_PET",
    payload: {
      petId: pet.id,
      petCategory: pet.category,
      petName: pet.name,
      petAge: pet.age,
      petGender: pet.gender,
      petPrice: pet.price,
      petCondition: pet.condition,
      petImage: pet.image,
    },
  };
};

export const updatePet = (pet) => {
  return {
    type: "UPDATE_PET",
    payload: {
      petId: pet.id,
      petCategory: pet.category,
      petName: pet.name,
      petAge: pet.age,
      petGender: pet.gender,
      petPrice: pet.price,
      petCondition: pet.condition,
      petImage: pet.image,
    },
  };
};

