import {
  CREATE_CATEGORY,
  UPDATE_CATEGORY,
  DELETE_CATEGORY,
} from "../actions/categoryActions";

const initialState = {
  tags: [
    // {
    //   id: 1,
    //   name: "Chó",
    //   image:
    //     "https://www.flaticon.com/free-icon/happy_2171990?related_id=2171990&origin=tag",
    // },
    // {
    //   id: 2,
    //   name: "Mèo",
    //   image:
    //     "https://www.flaticon.com/free-icon/laughing_2171987?term=cat&page=1&position=17&page=1&position=17&related_id=2171987&origin=search",
    // },
    // {
    //   id: 3,
    //   name: "Cá",
    //   image:
    //     "https://www.flaticon.com/free-icon/fish_2970068?term=fish&page=1&position=2&page=1&position=2&related_id=2970068&origin=search",
    // },
  ],
  currentTag: {},
};

export const categoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case CREATE_CATEGORY:
      let newTag = {
        id: action.payload.tagId,
        name: action.payload.tagName,
        icon: action.payload.tagImage,
      };
      return {
        ...state,
        tags: [...state.tags, newTag],
        currentTag: newTag,
      };
    case UPDATE_CATEGORY:
      return {
        ...state,
        tags: state.tags.map((x) => {
          if (x.id === action.payload.tagId) {
            return {
              ...x,
              name: action.payload.tagName,
              icon: action.payload.tagImage,
            };
          }
          return x;
        }),
      };
    case DELETE_CATEGORY:
      return {
        ...state,
        tags: state.tags.filter((x) => x.id !== action.payload.tagId),
      };
    default:
      return { ...state };
  }
};
