import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  TextInput,
  ImageBackground
} from "react-native";
//import { Ionicons,FontAwesome5 } from "@expo/vector-icons";
import Ionicons from "react-native-vector-icons/Ionicons";
// step 1
import { useDispatch, useSelector } from "react-redux";
import { createCategory } from "../redux/actions/categoryActions";
import AsyncStorage from "@react-native-async-storage/async-storage";

const CreateCategory = ({ navigation }) => {
  // step 2
  const dispatch = useDispatch();
  const db = useSelector((state) => state.tags);

  const [id, setId] = useState(0);
  const [name, setName] = useState("");
  const [icon, setIcon] = useState("");

  const handleSaveTag = () => {
    let newtag = {
      id: id,
      name: name,
      icon: icon,
    };
    console.log(newtag);
    //step 3
    dispatch(createCategory(newtag));
    navigation.navigate("ViewAll");
  };
  return (
    <View style={styles.loginContainer}>
      <ImageBackground
        source={{
          uri: "https://i.pinimg.com/originals/2e/e9/18/2ee918427712255bc116749e33616d33.png",
        }}
        resizeMode="cover"
        style={styles.bgContainer}
      >
        <View style={styles.logoLogin}>
          <Ionicons name="create" color="#FFF" size={36} />
        </View>
        <Text style={styles.signinText}>Thêm danh mục</Text>
        <View style={styles.formContainer}>
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="ID Category"
              style={styles.inputText}
              onChangeText={(val) => setId(val)}
            />
          </View>
          {/* check val */}
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Name Category"
              style={styles.inputText}
              onChangeText={(val) => setName(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Hình ảnh"
              style={styles.inputText}
              onChangeText={(val) => setImage(val)}
            />
          </View>
          <TouchableOpacity style={styles.btn} onPress={() => handleSaveTag()}>
            <Text style={styles.btnTxt}>Save</Text>
          </TouchableOpacity>
        </View>
      </ImageBackground>
    </View>
  );
};

export default CreateCategory;

const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
  },
  logoLogin: {
    width: 60,
    height: 60,
    borderRadius: 60 / 2,
    backgroundColor: "#d81b60",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
  },
  signinText: {
    color: "#d81b60",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10,
    color: "#FFF",
  },
  formContainer: {
    width: "80%",
    alignItems: "center",
    justifyContent: "center",
    paddingTop: 50,
  },
  inputContainer: {
    width: "70%",
    marginBottom: 10,
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#d81b60",
    paddingVertical: 10,
    color: "#FFF",
    paddingLeft: 15,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
    marginTop: 50,
  },
  btnTxt: {
    color: "#FFF",
  },
  bgContainer: {
    flex: 1,
    alignItems: "center",
  },
});
