import React from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
  TextInput,
  Image,
  ScrollView,
} from "react-native";

//import { Ionicons,FontAwesome5 } from "@expo/vector-icons";
import Ionicons from "react-native-vector-icons/Ionicons";
//step1
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const ViewAllCategory = ({ navigation }) => {
  const db = useSelector((state) => state.tags);
  console.log(db);
  return (
    <View style={styles.CRUDContainer}>
      <View>
        <Text style={styles.tiltle}> CATEGORY:</Text>
      </View>
      <View style={styles.ProductAllContainer}>
        {db.tags ? (
          db.tags.map((tag) => {
            return (
              <TouchableOpacity
                key={tag.id}
                style={styles.tagContainer}
                onPress={() =>
                  navigation.navigate("Edit", {
                    tagId: tag.id,
                    tagName: tag.name,
                    tagImage: tag.image,
                  })
                }
              >
                <View style={styles.contentMain}>
                <Image source={{ uri: tag.image }} style={styles.img} />
                  <View style={styles.TxtView}>
                    <Text style={styles.tagText}>{tag.name}</Text>
                  </View>
                  <Image source={{ uri: tag.image }} style={styles.img} />
                </View>
              </TouchableOpacity>
            );
          })
        ) : (
          <View>
            <Text>No data</Text>
          </View>
        )}
        <View style={{ alignItems: "center", justifyContent: "center" }}>
          <TouchableOpacity
            style={styles.btn}
            onPress={() => {
              navigation.navigate("Create");
            }}
          >
            <Ionicons
              style={styles.notiBtn}
              name="add"
              color="#fff"
              size={24}
            />
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
};
export default ViewAllCategory;
const styles = StyleSheet.create({
  CRUDContainer: {
    flex: 1,
    alignItems: "center",
    marginTop: 4,
  },
  bgContainer: {
    height: "auto",
  },
  contentMain: {
    flexDirection: "row",
  },
  TxtView: {
    marginRight: 40,
  },
  ProductAllContainer: {
    width: "100%",
    // alignItems: "center",
    //flexWrap: "wrap",
  },
  tagContainer: {
    alignItems: "flex-start",
    justifyContent: "center",
    width: "80%",
    height: 160,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#d81b60",
    padding: 10,
    margin: 10,
  },
  tagCT: {
    width: "auto",
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    padding: 10,
    margin: 10,
    borderRadius: 25,
    borderWidth: 1,
    borderColor: "#fff",
    backgroundColor: "#fff",
  },
  tagText: {
    color: "#d81b60",
    fontSize: 25,
    width: 120,
  },
  tagPText: {
    color: "#fff",
  },
  btn: {
    backgroundColor: "#d81b60",
    width: 45,
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
  },
  btnText: {
    color: "#FFF",
  },
  img: {
    width: 120,
    height: 120,
    borderRadius: 20,
    alignItems: "flex-end",
  },
  tiltle: {
    paddingTop: 20,
    fontSize: 30,
    fontWeight: "bold",
    color: "red",
  },
  tagss: {
    fontSize: 10,
    color: "#000",
  },
  tagAllContainer: {
    flexDirection: "row",
    alignSelf: "flex-start",
  },
});
