export const CREATE_PET = "CREATE_PET";
export const UPDATE_PET = "UPDATE_PET";
export const DELETE_PET = "DELETE_PET";
import categoryActions from './'

//action creator
export const createCategory = (pet) => {
  return {
    type: "CREATE_PET",
    payload: {
      petId: pet.id,
      petCategory: pet.category,
      petName: pet.name,
      petAge: pet.age,
      petGender: pet.gender,
      petPrice: pet.price,
      petCondition: pet.condition,
      petImage: pet.image,
    },
  };
};

export const updateCategory = (tag) => {
  return {
    type: "UPDATE_PET",
    payload: {
      tagId: tag.id,
      tagCategory: tag.category,
      tagName: tag.name,
      tagAge: tag.age,
      tagGender: tag.gender,
      tagPrice: tag.price,
      tagCondition: tag.condition,
      tagImage: tag.image,
    },
  };
};

export const deleteCategory = (id) => {
  return {
    type: "DELETE_PET",
    payload: {
      tagId: id,
    },
  };
};
