import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  TextInput,
  ImageBackground,
  ScrollView,
} from "react-native";
//import { Ionicons,FontAwesome5 } from "@expo/vector-icons";
import Ionicons from "react-native-vector-icons/Ionicons";
// step 1
import { useDispatch, useSelector } from "react-redux";
import { createItem } from "../redux/actions/itemActions";
import AsyncStorage from "@react-native-async-storage/async-storage";

const CreateItem = ({ navigation }) => {
  // step 2
  const dispatch = useDispatch(navigation);
  const db = useSelector((state) => state.items);

  const [id, setId] = useState(0);
  const [name, setName] = useState("");
  const [des, setDes] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState("");

  const handleSaveTag = () => {
    let newTag = {
      id: id,
      name: name,
      price: price,
      quantity: quantity,
      des: des,
      image: image,
    };
    // console.log(newTag);
    //step 3
    dispatch(createItem(newTag));
    navigation.navigate("ViewAllItem");
  };
  return (
    <View style={styles.loginContainer}>
      <ImageBackground
        source={require("../assets/BK.jpg")}
        resizeMode="cover"
        style={styles.bgContainer}
      >
        <View style={styles.logoLogin}>
          <Ionicons name="create" color="#FFF" size={36} />
        </View>
        <Text style={styles.signinText}>ADD ITEMS</Text>
        <ScrollView style={styles.formContainer}>
          {/* <View style={styles.inputContainer}>
            <TextInput
              placeholder="Pet ID"
              style={styles.inputText}
              onChangeText={(val) => setId(val)}
            />
          </View> */}
          {/* <View style={styles.inputContainer}>
            <TextInput
              placeholder="Pet Category"
              style={styles.inputText}
              onChangeText={(val) => setCategory(val)}
            />
          </View> */}
          {/* check val */}
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Item Name"
              style={styles.inputText}
              onChangeText={(val) => setName(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Item Price"
              style={styles.inputText}
              onChangeText={(val) => setPrice(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Item Quantity"
              style={styles.inputText}
              onChangeText={(val) => setQuantity(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Item Des"
              style={styles.inputText}
              onChangeText={(val) => setDes(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              placeholder="Item Image "
              style={styles.inputText}
              onChangeText={(val) => setImage(val)}
            />
          </View>
          <TouchableOpacity style={styles.btn} onPress={() => handleSaveTag()}>
            <Text style={styles.btnTxt}>Save</Text>
          </TouchableOpacity>
        </ScrollView>
      </ImageBackground>
    </View>
  );
};

export default CreateItem;

const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
  },
  logoLogin: {
    width: 60,
    height: 60,
    borderRadius: 60 / 2,
    backgroundColor: "#d81b60",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 20,
  },
  signinText: {
    color: "#d81b60",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10,
  },
  formContainer: {
    width: "80%",
    marginLeft: 88,
    // alignItems: "center",
    // justifyContent: "center",
    paddingTop: 10,
  },
  inputContainer: {
    width: "70%",
    marginBottom: 10,
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#d81b60",
    paddingVertical: 10,
    color: "#212121",
    paddingLeft: 15,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
    marginTop: 10,
  },
  btnTxt: {
    color: "#FFF",
  },
  bgContainer: {
    flex: 1,
    alignItems: "center",
  },
});
