import React, { useState, useEffect } from "react";
import {
  FlatList,
  StyleSheet,
  Text,
  View,
  Image,
  // Dimensions,
  ImageBackground,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchAllCategory,
  SearchCate,
  deleteCate,
} from "../redux/actions/categoryActions";
import { Ionicons, AntDesign } from "@expo/vector-icons";

const ListItemView = ({ navigation,route }) => {
  const db = useSelector((store) => store.categories);
  const dispatch = useDispatch();
  const { productId } = route.params;
  const [docId, setdocId] = useState(productId);
  console.log(db);
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const handleSearchCate = (key) => {
    dispatch(SearchCate(key));
  };
  useEffect(() => {
    dispatch(fetchAllCategory());
    // setData(db.courses)
    console.log(data);
  }, []);

  const del = (docId) => {
    dispatch(deleteCate(docId));
    navigation.navigate("ViewAll");
  };

  // const HeaderComponent = () => {
  //   return (
  //     <View style={styles.headerListComponent}>
  //       <Text style={styles.headerComponentTitle}>LIST CATEGORY</Text>
  //     </View>
  //   );
  // };
  const ItemComponent = ({ item }) => {
    return (
      <View key={item.docId} style={styles.tagContainer}>
        <View style={styles.contentMain}>
          <Image source={{ uri: item.Img }} style={styles.img} />
          <View style={styles.View}>
            <Text style={styles.tagText}>{item.name}</Text>
            <View>
              <TouchableOpacity
                style={styles.btnUpdate}
                onPress={() =>
                  navigation.navigate("Edit", {
                    CateId: item.docId,
                    CateImg: item.Img,
                    CateName: item.name,
                  })
                }
              >
                <AntDesign name="edit" size={24} color="black" />
                <Text style={styles.txtUpdate}>Edit</Text>
              </TouchableOpacity>
            </View>
            <View style={{ marginTop: 10 }}>
              <TouchableOpacity
                style={styles.btnView}
                onPress={() => navigation.navigate("ViewAllPet")}
              >
                <Ionicons name="search" size={24} color="black" />
                <Text style={styles.txtView}>View List</Text>
              </TouchableOpacity>
            </View>
            <View style={{ marginTop: 10 }}>
              <TouchableOpacity
                style={styles.btnDel}
                onPress={() => del(docId)}
              >
                <AntDesign name="delete" size={24} color="black" />
                <Text style={styles.txtDel}>Delete</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </View>
    );
  };
  return (
    <View style={styles.ListItemContainer}>
      <ImageBackground
        style={styles.background}
        resizeMode="cover"
        source={{
          uri: "https://i.pinimg.com/originals/2e/e9/18/2ee918427712255bc116749e33616d33.png",
        }}
      >
        <View style={styles.leftHeader}>
          <TextInput
            placeholder="Search"
            style={styles.inputText}
            onChangeText={(text) => setSearch(text)}
          />
          <TouchableOpacity onPress={() => handleSearchCate(search)}>
            <Text style={styles.search}>Tìm kiếm</Text>
          </TouchableOpacity>
        </View>
        <Text style={styles.headerComponentTitle}>LIST CATEGORY</Text>

        <FlatList
          data={db.categories}
          //ListHeaderComponent={HeaderComponent}
          renderItem={ItemComponent}
        ></FlatList>
        <TouchableOpacity
          style={styles.btnAdd}
          onPress={() => navigation.navigate("Create")}
        >
          <Text
            style={{
              justifyContent: "center",
              alignSelf: "center",
              fontSize: 40,
            }}
          >
            +
          </Text>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  );
};

export default ListItemView;
//const width = Dimensions.get("window").width - 20;
const styles = StyleSheet.create({
  ListItemContainer: {
    flex: 1,
    backgroundColor: "#eeeeee",
  },
  background: {
    flex: 1,
    padding: 20,
  },
  btnAdd: {
    alignSelf: "flex-end",
    width: 60,
    height: 60,
    borderRadius: 30,
    backgroundColor: "#fff",
    opacity: 0.8,
  },
  ItemComponent: {
    marginTop: 50,
    //width: width / 2,
    padding: 5,

    borderColor: "#000",
    borderWidth: 1,
    shadowColor: "#000",
    shadowOffset: {
      width: 10,
      height: 15,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.5,
    elevation: 5,
    alignItems: "center",
    justifyContent: "center",
  },
  img: {
    width: 120,
    height: 120,
    borderRadius: 20,
    alignSelf: "center",
  },
  ItemDetail: {
    textAlign: "center",
    width: "70%",
  },
  nameTxt: {
    color: "white",
    marginTop: 5,
    fontSize: 18,
    flexWrap: "wrap",
  },
  priceTxt: {
    color: "#64b5f6",
    fontSize: 18,
  },
  detailTxt: {
    color: "black",
    fontSize: 15,
  },
  rating: {
    color: "#f4511e",
  },
  headerComponentTitle: {
    color: "#fff",
    fontSize: 30,
    fontWeight: "bold",
    paddingTop: 10,
    textAlign: "center",
  },
  headerListComponent: {
    textAlign: "center",
  },
  leftHeader: {
    width: "88%",
    justifyContent: "space-between",
    alignItems: "center",
    flexDirection: "row",
    height: 50,
    marginTop: 10,
  },
  inputText: {
    justifyContent: "center",
    alignItems: "center",
    color: "#000",
    borderRadius: 50 / 2,
    borderColor: "#000",
    borderWidth: 2,
    width: "80%",
    height: 50,
    paddingLeft: "6%",
    fontSize: 20,
    fontWeight: "500",
    backgroundColor: "white",
  },
  search: {
    fontSize: 20,
    color: "black",
    paddingStart: 10,
    backgroundColor: "white",
    fontWeight: "bold",
  },
  tagContainer: {
    alignItems: "flex-start",
    justifyContent: "center",
    width: "80%",
    height: 160,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#d81b60",
    paddingLeft: 10,
    margin: 10,
  },
  contentMain: {
    flexDirection: "row",
    alignItems: "center",
  },
  View: {
    marginLeft: 24,
    marginBottom: 28,
    justifyContent: "center",
  },
  tagText: {
    color: "#d81b60",
    fontSize: 35,
  },
  btnUpdate: {
    backgroundColor: "#ef9a9a",
    borderRadius: 5,
    flexDirection: "row",
  },
  btnView: {
    backgroundColor: "#b39ddb",
    borderRadius: 5,
    flexDirection: "row",
  },
  btnDel: {
    backgroundColor: "#ec407a",
    borderRadius: 5,
    flexDirection: "row",
  },
  txtUpdate: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
  txtView: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
  txtDel: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
});
