import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
  TextInput,
  Image,
  ScrollView, 
  Modal,
  Pressable,
} from "react-native";

import { Ionicons, AntDesign } from "@expo/vector-icons";
import { fetchAllPet, searchPet } from "../redux/actions/petActions";
//step1
import { useDispatch, useSelector } from "react-redux";
//import ReadMore from "@fawazahmed/react-native-read-more";

const ListItemView = ({ navigation }) => {
  const dispatch = useDispatch();
  const [ModalVisible, setModalVisible] = useState(false);
  const db = useSelector((store) => store.pets);
  console.log(db);
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    dispatch(fetchAllPet());
    setData(db.pets);
    console.log("all pet", data);
  }, []);
  const Search = (key) => {
    dispatch(searchPet(key));
  };
  return (
    <View style={styles.CRUDContainer}>
      <ImageBackground
        style={styles.background}
        resizeMode="cover"
        source={{
          uri: "https://t3.ftcdn.net/jpg/00/84/55/66/360_F_84556601_W71hd1xmCxZUhsscyNlokY1an7Kqx6ZJ.jpg",
        }}
      >
        <View style={styles.leftHeader}>
          <TextInput
            placeholder="Search"
            style={styles.inputText}
            onChangeText={(text) => setSearch(text)}
          />
          <TouchableOpacity
            onPress={() => Search(search)}
            style={styles.btnSearch}
          >
            <Text style={styles.search}>Tìm kiếm</Text>
          </TouchableOpacity>
        </View>
        <View>
          <Text style={styles.title}>Danh sách thú cưng</Text>
        </View>
        <ScrollView style={styles.ProductAllContainer}>
          {db.pets ? (
            db.pets.map((item, index) => {
              return (
                <View key={index} style={styles.tagContainer}>
                  <View style={styles.contentMain}>
                    <Image source={item.Image} style={styles.img} />
                    <View style={styles.View}>
                      <Text style={styles.textName}>Tên: {item.Name}</Text>
                      <Text style={styles.textPrice}>
                        Giá: {item.Price} VNĐ
                      </Text>
                      <View>
                  
                      </View>
                      <View style={{ marginTop: 10 }}>
                        <TouchableOpacity
                          style={styles.btnView}
                          onPress={() => {
                            setModalVisible(true);
                          }}
                        >
                          <Ionicons name="search" size={24} color="black" />
                          <Text style={styles.txtView}>View Detail</Text>
                        </TouchableOpacity>
                      </View>
                      <View style={styles.centeredView}>
                        <Modal
                          animationType="slide"
                          transparent={true}
                          visible={ModalVisible}
                          onRequestClose={() => setModalVisible(false)}
                        >
                          <Pressable
                            style={styles.outsideModal}
                            onPress={(event) => {
                              if (event.target == event.currentTarget) {
                                setModalVisible(false);
                              }
                            }}
                          >
                            <View style={styles.modal}>
                              <View style={styles.imageContainer}>
                                <View style={styles.image}>
                                  <Image
                                    style={styles.imgInfo}
                                    source={item.Image}
                                  />
                                </View>
                                <View style={styles.text}>
                                  <Text style={styles.txtName}>
                                    {item.Name}
                                  </Text>
                                  <Text style={styles.txtPrice}>
                                    {item.Price} VNĐ
                                  </Text>

                                  <TouchableOpacity
                                    title="Add to cart"
                                    onPress={() => navigation.navigate("Cart")}
                                  ></TouchableOpacity>
                                </View>
                              </View>
                              <Text style={styles.text4}>
                                Thông tin chi tiết
                              </Text>
                              <View style={styles.description}>
                                <View>
                                  {/* <Text style={styles.textTitle}>ID: </Text> */}
                                  {/* <Text style={styles.textTitle}>Type:</Text> */}
                                  <Text style={styles.textTitle}>Age:</Text>
                                  <Text style={styles.textTitle}>Gender:</Text>
                                  <Text style={styles.textTitle}>
                                    Condition:
                                  </Text>
                                  <Text style={styles.textTitle}>Origin:</Text>
                                </View>
                                <View>
                                  {/* <Text style={styles.text3}>{item.Id}</Text> */}
                                  {/* <Text style={styles.text3}>{item.Type}</Text> */}
                                  <Text style={styles.text3}>{item.Age}</Text>
                                  <Text style={styles.text3}>
                                    {item.Gender}
                                  </Text>
                                  <Text style={styles.text3}>
                                    {item.Condition}
                                  </Text>
                                  <Text style={styles.text3}>
                                    {item.Origin}
                                  </Text>
                                </View>
                              </View>
                       
                              <TouchableOpacity
                                style={styles.btnClosingModal}
                                onPress={() => setModalVisible(false)}
                              >
                                <Text style={styles.btnText}>Back</Text>
                              </TouchableOpacity>
                            </View>
                          </Pressable>
                        </Modal>
                      </View>
                    </View>
                  </View>
                </View>
              );
            })
          ) : (
            <View>
              <Text>No data</Text>
            </View>
          )}
        </ScrollView>

      </ImageBackground>
    </View>
  );
};
