import React, { useState, useEffect } from "react";
import {
  Text,
  FlatList,
  View,
  StyleSheet,
  TouchableOpacity,
  Image,
  ImageBackground,
  ScrollView,
  TextInput,
} from "react-native";
import { fetchAllItem, findItem } from "../redux/actions/itemActions";
import { Ionicons, AntDesign } from "@expo/vector-icons";
//step1
import { useDispatch, useSelector } from "react-redux";

const Search = ({ navigation, route }) => {
  const db = useSelector((store) => store.items);
  const dispatch = useDispatch();
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const searchItem = (key) => {
    dispatch(findItem(key));
  };
  useEffect(() => {
    dispatch(fetchAllItem());
    setData(db.item);
    console.log(data);
  }, []);

  const HeaderComponent = () => {
    return (
      <View style={styles.headerListComponent}>
        <Text style={styles.HeaderTitle}>LIST ITEM</Text>
      </View>
    );
  };
  const ItemComponent = ({ item }) => {
    return (
      <TouchableOpacity
        key={item.docId}
        style={styles.ItemComponent}
        onPress={() => {
          navigation.navigate("EditItem", {
            productCId: item.cateID,
            productId: item.id,
            productName: item.name,
            productPrice: item.price,
            productQuantity: item.quantity,
            productImage: item.image,
          });
        }}
      >
        <Image source={{ uri: item.image }} style={styles.img} />

        <View style={styles.itemDetail}>
          <Text style={styles.name}>Tên: {item.name}</Text>
          <Text style={styles.price}>Giá: {item.price} VNĐ</Text>
          <Text style={styles.quantity}>Số lượng: {item.quantity}</Text>
        </View>
      </TouchableOpacity>
    );
  };
  return (
    <View style={styles.container}>
      <ImageBackground
        style={styles.background}
        resizeMode="cover"
        source={{
          uri: "https://t3.ftcdn.net/jpg/00/84/55/66/360_F_84556601_W71hd1xmCxZUhsscyNlokY1an7Kqx6ZJ.jpg",
        }}
      >
        <View style={styles.searchView}>
          <TextInput
            placeholder="Search"
            style={styles.inputText}
            onChangeText={(text) => setSearch(text)}
          />
          <TouchableOpacity
            style={styles.btnSearch}
            onPress={() => searchItem(search)}
          >
            <Text style={styles.searchTxt}>Tìm kiếm</Text>
          </TouchableOpacity>
        </View>
        <FlatList
          data={db.items}
          ListHeaderComponent={HeaderComponent}
          renderItem={ItemComponent}
          
        >
              <TouchableOpacity
                style={{ flexDirection: "row" }}
                onPress={() => {
                  setModalVisible(true);
                }}
              >
                <Ionicons name="search" size={20} color="black" />
                <Text>View detail</Text>
              </TouchableOpacity>
        </FlatList>
    
      </ImageBackground>
    </View>
  );
};
export default Search;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#eeeeee",
  },
  background: {
    flex: 1,
    padding: 20,
  },
  searchView: {
    width: "88%",
    justifyContent: "space-between",
    alignItems: "center",
    flexDirection: "row",
    height: 50,
    marginTop: 10,
  },
  inputText: {
    justifyContent: "center",
    alignItems: "center",
    color: "#000",
    borderRadius: 50 / 2,
    borderColor: "#000",
    borderWidth: 2,
    width: "80%",
    height: 50,
    paddingLeft: "6%",
    fontSize: 20,
    fontWeight: "500",
    backgroundColor: "white",
  },
  btnSearch: {
    backgroundColor: "white",
    borderRadius: 10,
    marginLeft: 10,
    justifyContent: "center",
    alignItems: "center",
    height: 30,
    borderWidth: 2,
  },
