import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  TextInput,
} from "react { useState }-native";
import { Ionicons } from "@expo/vector-icons";
// step 1
import { useDispatch, useSelector } from "react-redux";
import { createCategory } from "../redux/actions/categoryActions";
import AsyncStorage from "@react-native-async-storage/async-storage";

const CreateCategory = ({ navigation }) => {
  // step 2
  const dispatch = useDispatch();
  const db = useSelector((state) => state.tags);

  const [id, setId] = useState(0);
  const [name, setName] = useState("");
  const [icon, setIcon] = useState("");

  const handleSaveTag = () => {
    let newtag = {
      id: id,
      name: name,
      icon: icon,
    };
    console.log(newtag);
    //step 3
    dispatch(createCategory(newtag));
    navigation.navigate("ViewAll");
  };
  return (
    <View style={styles.CRUDContainer}>
      <View style={styles.formContainer}>
        <View style={styles.inputContainer}>
          <TextInput
            placeholder="Category Id"
            style={styles.inputText}
            onChangeText={(val) => setId(val)}
          />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            placeholder="Category Name"
            style={styles.inputText}
            onChangeText={(val) => setName(val)}
          />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            placeholder="Category Icon"
            style={styles.inputText}
            onChangeText={(val) => setIcon(val)}
          />
        </View>
        <TouchableOpacity style={styles.btn} onPress={() => handleSaveTag()}>
          <Text style={styles.btnText}>Save</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default CreateCategory;

const styles = StyleSheet.create({
  CRUDContainer: {
    flex: 1,
  },
  formContainer: {
    // backgroundColor: 'black'
    width: "100%",
    alignItems: "center",
  },
  inputContainer: {
    width: "70%",
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#d81b60",
    paddingVertical: 5,
    marginBottom: 10,
    fontSize: 17,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
  },
  btnText: {
    color: "#fff",
  },
  listItemContainer: {
    flex: 1,
  },
  headerComponentTitle: {
    color: "black",
    fontSize: 30,
  },
  headerListComponent: {
    textAlign: "center",
    marginTop: 10,
    marginBottom: 20,
  },
  itemContainer: {
    flexDirection: "row",
    marginTop: 10,
    marginLeft: 15,
  },
  imgItem: {
    width: 100,
    height: 100,
  },
  itemInfo: {},
  itemDetails: {
    marginLeft: 15,
    marginBottom: 30,
    width: "65%",
  },
  nameText: {
    color: "#000",
    fontSize: 22,
    flexWrap: "wrap",
  },
  descriptionText: {
    color: "#bdbdbd",
    fontSize: 22,
  },
  ratingText: {
    color: "#000",
    fontSize: 22,
  },
  priceText: {
    color: "#000",
    fontSize: 22,
  },
});
