  },
  ProductAllContainer: {
    width: "100%",
    //flexDirection: "row",
    // alignItems: "center",
    //flexWrap: "wrap",
  },
  tagContainer: {
    width: 340,
    height: 160,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#d81b60",
    paddingLeft: 10,
    margin: 10,
  },
  tagCT: {
    width: "auto",
