import React from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
  TextInput,
  Image,
  ScrollView,
  Modal,
} from "react-native";

import { Ionicons, AntDesign } from "@expo/vector-icons";
//import Ionicons from "react-native-vector-icons/Ionicons";
//step1
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const ViewAllCategory = ({ navigation }) => {
  const db = useSelector((state) => state.pets);
  console.log(db);
  return (
    <View style={styles.CRUDContainer}>
      <View>
        <Text style={styles.tiltle}>LIST PET</Text>
      </View>
      <View style={styles.ProductAllContainer}>
        {db.pets ? (
          db.pets.map((pet) => {
            return (
              <View key={pet.id} style={styles.tagContainer}>
                <View style={styles.contentMain}>
                  <Image source={{ uri: pet.image }} style={styles.img} />
                  <View style={styles.View}>
                    <Text style={styles.textName}>{pet.name}</Text>
                    <Text style={styles.textPrice}>{pet.price}</Text>
                    <View>
                      <TouchableOpacity
                        style={styles.btnUpdate}
                        onPress={() =>
                          navigation.navigate("Edit", {
                            petId: pet.id,
                            petName: pet.name,
                            petPrice: pet.price,
                            petImage: pet.image,
                          })
                        }
                      >
                        <AntDesign name="edit" size={24} color="black" />
                        <Text style={styles.txtUpdate}>Update</Text>
                      </TouchableOpacity>
                    </View>
                    <View style={{ marginTop: 10 }}>
                      <TouchableOpacity
                        style={styles.btnView}
                        onPress={() => navigation.navigate("ViewAllPet")}
                      >
                        <Ionicons name="search" size={24} color="black" />
                        <Text style={styles.txtView}>View Detail</Text>
                      </TouchableOpacity>
                    </View>
                  </View>
                </View>
              </View>
            );
          })
        ) : (
          <View>
            <Text>No data</Text>
          </View>
        )}
      </View>
      <View style={{ alignItems: "center", justifyContent: "center" }}>
        <TouchableOpacity
          style={styles.btn}
          onPress={() => {
            navigation.navigate("CreatePet");
          }}
        >
          <Ionicons style={styles.notiBtn} name="add" color="#fff" size={24} />
        </TouchableOpacity>
      </View>
    </View>
  );
};
export default ViewAllCategory;
const styles = StyleSheet.create({
  CRUDContainer: {
    flex: 1,
    alignItems: "center",
    marginTop: 4,
  },
  bgContainer: {
    height: "auto",
  },
  contentMain: {
    flexDirection: "row",
  },
  View: {
    marginLeft: 24,
    marginBottom: 28,
    justifyContent: "center",
  },
  ProductAllContainer: {
    width: "100%",
    //flexDirection: "row",
    // alignItems: "center",
    //flexWrap: "wrap",
  },
  tagContainer: {
    alignItems: "flex-start",
    justifyContent: "center",
    width: "80%",
    height: 160,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#d81b60",
    paddingLeft: 10,
    margin: 10,
  },
  tagCT: {
    width: "auto",
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    padding: 10,
    margin: 10,
    borderRadius: 25,
    borderWidth: 1,
    borderColor: "#fff",
    backgroundColor: "#fff",
  },
  textName: {
    color: "#d81b60",
    fontSize: 25,
  },
  textPrice: {
    color: "#d81b60",
    fontSize: 35,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: 45,
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
  },
  img: {
    width: 120,
    height: 120,
    borderRadius: 20,
    marginTop: 6,
  },
  tiltle: {
    paddingTop: 20,
    fontSize: 30,
    fontWeight: "bold",
    color: "red",
  },
  tagss: {
    fontSize: 10,
    color: "#000",
  },
  tagAllContainer: {
    flexDirection: "row",
    alignSelf: "flex-start",
  },
  btnUpdate: {
    backgroundColor: "#ef9a9a",
    borderRadius: 5,
    flexDirection: "row",
  },
  btnView: {
    backgroundColor: "#b39ddb",
    borderRadius: 5,
    flexDirection: "row",
  },
  txtUpdate: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
  txtView: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
});
