export const updateCategory = (Category) => {
  return {
    type: "UPDATE_CATEGORY",
    payload: Category,
  };
};

export const deleteCategory = (id) => {
  return {
    type: "DELETE_CATEGORY",
    payload: {
      categoryId: id,
    },
  };
};

export const getAllCategory = (Category) => {
  return {
    type: "GETALL_CATEGORY",
    payload: Category,
  };
};

export const fetchAllCategory = () => {
  return (dispatch) => {
    const getData = async () => {
      try {
        const response = await fetch("http://localhost:5555/api/Category");
        const category = await response.json();
        console.log("here", category);
        //dispatch action to reducer
        dispatch(getAllCategory(category));
      } catch (err) {
        console.error(err);
      }
    };
    getData();
  };
};
export const createCategory = (Category) => {
  return {
    type: "CREATE_CATEGORY",
    payload: Category,
  };
};

export const fetchCreateCategory = (Category) => {
  return (dispatch) => {
    const addData = async () => {
      try {
        await fetch("http://localhost:5555/api/addCategory", {
          method: "POST",
          header: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify(Category),
        });
        const category = await response.json();
        console.log("addHere", category);
      } catch (err) {
        console.error(err);
      }
    };
    addData();
    //dispatch action to reducer
    dispatch(createCategory(Category));
  };
};

export const fetchUpdateCategory = (Category) => {
  return (dispatch) => {
    const updateData = async () => {
      try {
        await fetch("http://localhost:5555/api/updateCategory", {
          method: "PUT",
          header: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify(Category),
        });
        const category = await response.json();
        console.log("updateHere", category);
      } catch (err) {
        console.error(err);
      }
    };
    updateData();
    //dispatch action to reducer
    dispatch(updateCategory(Category));
  };
};

export const fetchDeleteCategory = (Category) => {
  return (dispatch) => {
    const deleteData = async () => {
      try {
        await fetch("http://localhost:5555/api/deleteCategory", {
          method: "DELETE",
          header: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify(Category),
        });
        const category = await response.json();
        console.log("deleteHere", category);
      } catch (err) {
        console.error(err);
      }
    };
    deleteData();
    //dispatch action to reducer
    dispatch(deleteCategory(Category));
  };
};
