import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  ScrollView
} from "react-native";
//import Ionicons from "react-native-vector-icons/Ionicons";
import { Ionicons, FontAwesome5 } from "@expo/vector-icons";

import { useDispatch, useSelector } from "react-redux";
import { updatePet, deletePet } from "../redux/actions/petActions";

const EditPet = ({ navigation, route }) => {
  const { petId } = route.params;
  const { petCategory } = route.params;
  const { petName } = route.params;
  const { tagAge } = route.params;
  const { tagGender } = route.params;
  const { tagPrice } = route.params;
  const { tagCondition } = route.params;
  const { tagName } = route.params;

  const [id, setId] = useState(petId);
  const [category, setCategory] = useState(petCategory);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [price, setPrice] = useState(0);
  const [condition, setCondition] = useState("");
  const [image, setImage] = useState("");

  const dispatch = useDispatch();
  const db = useSelector((state) => state.pets);

  const handleUpdateTag = () => {
    let updateTag = {
      id: id,
      category: category,
      name: name,
      age: age,
      gender: gender,
      price: price,
      condition: condition,
      image: image,
    };
    dispatch(updatePet(updateTag));
    navigation.navigate("ViewAllPet");
  };

  const handleDeleteTag = (id) => {
    dispatch(deletePet(id));
    navigation.navigate("ViewAllPet");
  };

  return (
    <View style={styles.loginContainer}>
      <ImageBackground
        source={require("../assets/BK.jpg")}
        resizeMode="cover"
        style={styles.bgContainer}
      >
        <View style={styles.logoLogin}>
          <Ionicons name="create" color="#FFF" size={36} />
        </View>
        <Text style={styles.title}>EDIT PET</Text>
        <ScrollView style={styles.formContainer}>
          <View style={styles.inputContainer}>
            <TextInput
              value={id}
              placeholder="Pet ID"
              style={styles.inputText}
              onChangeText={(val) => setId(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={id}
              placeholder="Pet Category"
              style={styles.inputText}
              onChangeText={(val) => setCategory(val)}
            />
          </View>
          {/* check val */}
          <View style={styles.inputContainer}>
            <TextInput
              value={name}
              placeholder="Pet Name"
              style={styles.inputText}
              onChangeText={(val) => setName(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={name}
              placeholder="Pet Age"
              style={styles.inputText}
              onChangeText={(val) => setAge(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={name}
              placeholder="Pet Name"
              style={styles.inputText}
              onChangeText={(val) => setGender(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={name}
              placeholder="Pet Name"
              style={styles.inputText}
              onChangeText={(val) => setPrice(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={name}
              placeholder="Pet Name"
              style={styles.inputText}
              onChangeText={(val) => setCondition(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={image}
              placeholder="Pet Image"
              style={styles.inputText}
              onChangeText={(val) => setImage(val)}
            />
          </View>
          <TouchableOpacity
            style={styles.btn}
            onPress={() => handleUpdateTag()}
          >
            <Text style={styles.btnTxt}>Update</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.btn}
            onPress={() => handleDeleteTag(categoryId)}
          >
            <Text style={styles.btnTxt}>Delete</Text>
          </TouchableOpacity>
        </ScrollView>
      </ImageBackground>
    </View>
  );
};
export default EditPet;
const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
  },
  logoLogin: {
    width: 60,
    height: 60,
    borderRadius: 60 / 2,
    backgroundColor: "#d81b60",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 20,
  },
  title: {
    color: "#d81b60",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10,
  },
  formContainer: {
    width: "80%",
    marginLeft:88,
    // alignItems: "center",
    // justifyContent: "center",
    paddingTop: 10,
  },
  inputContainer: {
    width: "70%",
    marginBottom: 10,
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#d81b60",
    paddingVertical: 10,
    color: "#212121",
    paddingLeft: 15,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
    marginTop: 20,
  },
  btnTxt: {
    color: "#FFF",
  },
  bgContainer: {
    flex: 1,
    alignItems: "center",
  },
});
