import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ImageBackground,
} from "react-native";
//import Ionicons from "react-native-vector-icons/Ionicons";
import { Ionicons, FontAwesome5 } from "@expo/vector-icons";

import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import {
  updateCategory,
  deleteCategory,
} from "../redux/actions/categoryActions";

const EditCategory = ({ navigation, route }) => {
  const { tagId } = route.params;
  const { tagName } = route.params;

  const [id, setId] = useState(tagId);
  const [name, setName] = useState("");
  const [image, setImage] = useState("");

  const dispatch = useDispatch();
  const db = useSelector((state) => state.tags);

  const handleUpdateTag = () => {
    let updateTag = {
      id: id,
      name: name,
      image: image,
    };
    dispatch(updateCategory(updateTag));
    navigation.navigate("ViewAll");
  };

  const handleDeleteTag = (id) => {
    dispatch(deleteCategory(id));
    navigation.navigate("ViewAll");
  };

  return (
    <View style={styles.loginContainer}>
      <ImageBackground
        source={require("../assets/BK.jpg")}
        resizeMode="cover"
        style={styles.bgContainer}
      >
        <View style={styles.logoLogin}>
          <Ionicons name="create" color="#FFF" size={36} />
        </View>
        <Text style={styles.signinText}>Sửa món ăn</Text>
        <View style={styles.formContainer}>
          <View style={styles.inputContainer}>
            <TextInput
              value={id}
              plach
              style={styles.inputText}
              onChangeText={(val) => setId(val)}
            />
          </View>
          {/* check val */}
          <View style={styles.inputContainer}>
            <TextInput
              value={name}
              style={styles.inputText}
              onChangeText={(val) => setName(val)}
            />
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              value={image}
              style={styles.inputText}
              onChangeText={(val) => setImage(val)}
            />
          </View>
          <TouchableOpacity
            style={styles.btn}
            onPress={() => handleUpdateTag()}
          >
            <Text style={styles.btnTxt}>Update</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.btn}
            onPress={() => handleDeleteTag(tagId)}
          >
            <Text style={styles.btnTxt}>Delete</Text>
          </TouchableOpacity>
        </View>
      </ImageBackground>
    </View>
  );
};
export default EditCategory;
const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
  },
  logoLogin: {
    width: 60,
    height: 60,
    borderRadius: 60 / 2,
    backgroundColor: "#d81b60",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
  },
  signinText: {
    color: "#d81b60",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10,
    color: "#FFF",
  },
  formContainer: {
    width: "80%",
    alignItems: "center",
    justifyContent: "center",
    paddingTop: 50,
  },
  inputContainer: {
    width: "70%",
    marginBottom: 10,
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#d81b60",
    paddingVertical: 10,
    color: "#FFF",
    paddingLeft: 15,
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
    marginTop: 50,
  },
  btnTxt: {
    color: "#FFF",
  },
  bgContainer: {
    flex: 1,
    alignItems: "center",
  },
});
