import { CREATE_ITEM, UPDATE_ITEM, DELETE_ITEM } from "../actions/itemActions";

const initialState = {
  items: [],
  currentItem: {},
};

export const itemReducer = (state = initialState, action) => {
  switch (action.type) {
    case CREATE_ITEM:
      let newItem = {
        id: action.payload.itemId,
        name: action.payload.itemName,
        quantity: action.payload.itemQuantity,
        price: action.payload.itemPrice,
        des: action.payload.itemDes,
        image: action.payload.itemImage,
      };
      return {
        ...state,
        items: [...state.items, newItem],
        currentItem: newItem,
      };
    case UPDATE_ITEM:
      return {
        ...state,
        categories: state.categories.map((x) => {
          if (x.id === action.payload.itemId) {
            return {
              ...x,
              name: action.payload.itemName,
              quantity: action.payload.itemQuantity,
              price: action.payload.itemPrice,
              des: action.payload.itemDes,
              image: action.payload.itemImage,
            };
          }
          return x;
        }),
      };
    case DELETE_ITEM:
      return {
        ...state,
        items: state.items.filter((x) => x.id !== action.payload.itemId),
      };
    default:
      return { ...state };
  }
};
