import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  Image,
} from "react-native";

import { FontAwesome, Ionicons, FontAwesome5 } from "@expo/vector-icons";
import Icon from "react-native-vector-icons/FontAwesome";

const LoginView = ({ navigation }) => {
  return (
    <View style={styles.loginContainer}>
      <ImageBackground
        style={styles.bgContainer}
        source={require("../assets/background.jpg")}
        resizeMode="cover"
      >
        <ViewImage style={styles.logoLogin}>
          <Image source={require("../assets/pet.jpg")} resizeMode="stretch" />
        </ViewImage>
        <Text style={styles.signInText}>Sign in</Text>

        <View style={styles.formContainer}>
          <View style={styles.inputContainer}>
            <TextInput placeholder="Email" style={styles.inputText} />
          </View>
          <View style={styles.inputContainer}>
            <TextInput placeholder="Password" style={styles.inputText} />
          </View>
          <TouchableOpacity
            style={styles.btn}
            onPress={() => navigation.navigate("FloatList")}
          >
            <Text style={styles.btnText}>Submit</Text>
          </TouchableOpacity>
        </View>
      </ImageBackground>
    </View>
  );
};

export default LoginView;
const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
  },
  bgContainer: {
    flex: 1,
    alignItems: "center",
  },
  logoLogin: {
    width: 60,
    height: 60,
    borderRadius: 60 / 2,
    backgroundColor: "#d81b60",
    justifyContent: "center",
    alignItems: "center",
  },
  signInText: {
    color: "#d81b60",
    fontSize: 36,
    marginTop: 10,
  },
  formContainer: {
    // backgroundColor: "black",
    width: "100%",
    alignItems: "center",
  },
  inputContainer: {
    width: "70%",
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#b71c1c",
    paddingVertical: 5,
    marginBottom: 10,
  },
  btn: {
    backgroundColor: "#b71c1c",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
  },
  btnText: {
    color: "#fff",
  },
});
