import React, { useState, useEffect } from "react";
import {
  Platform,
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  Image
} from "react-native";
//import Ionicons from "react-native-vector-icons/Ionicons";
import { Ionicons, FontAwesome5 } from "@expo/vector-icons";
import {
  getStorage,
  uploadString,
  ref,
  getDownloadURL,
  uploadBytesResumable
} from "firebase/storage";

import * as ImagePicker from "expo-image-picker";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllItem } from "../redux/actions/itemActions";
import { ScrollView } from "react-native-virtualized-view";

const Item = ({ navigation, route }) => {
  const { productId } = route.params;
  const { productName } = route.params;
  const { productCId } = route.params;
  const { productImage } = route.params;
  const { productPrice } = route.params;
  const { productQuantity } = route.params;

  const [docId, setdocId] = useState(productId);
  const [id, setCId] = useState(productCId);
  const [name, setName] = useState(productName);
  const [quantity, setQuantity] = useState(productQuantity);
  const [price, setPrice] = useState(productPrice);

  const dispatch = useDispatch();
  const db = useSelector(store => store.items);

  useEffect(() => {
    dispatch(fetchAllItem());
    console.log("db", db);
  }, []);

  const [selectedImage, setSelectedImage] = useState({
    localURI: productImage
  });
  const convertURI2BlobFile = async uri => {
    const result = await new Promise((resolve, reject) => {
      let xmlRequest = new XMLHttpRequest();
      xmlRequest.onload = function() {
        resolve(xmlRequest.response);
      };
      xmlRequest.onerror = function() {
        reject(new TypeError("Request failed"));
      };
      xmlRequest.responseType = "blob";
      xmlRequest.open("GET", uri, true);
      xmlRequest.send(null);
    });
    return result;
  };
  const uploadFile = async blobFile => {
    let imgname = "img-android" + new Date().getTime();
    //step2
    let storage = getStorage();
    let storageRef = ref(storage, `images/${imgname}.jpg`);
    let metadata = { contentType: "image/jpeg" };

    const uploadTask = uploadBytesResumable(storageRef, blobFile, metadata);
    uploadTask.on(
      "state_changed",
      snapshot => {},
      error => {},
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then(downloadURL => {
          console.log("downloadURL", downloadURL);
          setSelectedImage({ localURI: downloadURL });
        });
      }
    );
  };
  const uploadBase64 = async base64code => {
    let imgname = "img-w-" + new Date().getTime();
    //step2
    let storage = getStorage();
    let storageRef = ref(storage, `images/${imgname}.jpg`);
    let metadata = { contentType: "image/jpeg" };
    uploadString(storageRef, base64code, "base64", metadata).then(snapshot => {
      getDownloadURL(snapshot.ref).then(downloadURL => {
        console.log("downloadURL", downloadURL);
        setSelectedImage({ localURI: downloadURL });
      });
    });
  };

  return (
    <View>
      <ImageBackground
        style={styles.bgContainer}
        source={require("../assets/BK.jpg")}
        resizeMode="cover"
      >
        <Image source={{ uri: selectedImage.localURI }} style={styles.img} />
        <View>
          {/* <Ionicons name="create" color="#FFF" size={36} /> */}
          <ion-icon name="heart-circle-outline" color="#FFF" size={36} />
        </View>
        <Text style={styles.signinText}>Thông tin</Text>
        <ScrollView
          showsVerticalScrollIndicator={false}
          style={styles.formContainer}
        >
          <Text style={styles.signinText1}>Tên sản phẩm</Text>
          <View style={styles.inputContainer}>
            <TextInput value={name} style={styles.inputText} />
          </View>
          <Text style={styles.signinText1}>Giá</Text>
          <View style={styles.inputContainer}>
            <TextInput value={price} style={styles.inputText} />
          </View>
          <Text style={styles.signinText1}>Số lượng</Text>
          <View style={styles.inputContainer}>
            <TextInput value={quantity} style={styles.inputText} />
          </View>
          <TouchableOpacity style={styles.btn}>
            <Text style={styles.btnTxt}>Thêm vào giỏ hàng</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.btn}>
            <Text style={styles.btnTxt}>Yêu thích</Text>
          </TouchableOpacity>
        </ScrollView>
      </ImageBackground>
    </View>
  );
};
export default Item;
const styles = StyleSheet.create({
  logoLogin: {
    width: 60,
    height: 60,
    borderRadius: 60 / 2,
    // backgroundColor: "#d81b60",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10
  },
  signinText: {
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10,
    color: "#d81b60"
  },
  signinText1: {
    fontSize: 20,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10,
    color: "black"
  },
  img: {
    width: 150,
    height: 150,
    borderRadius: 75
  },
  title: {
    color: "#d81b60",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
    marginTop: 10
  },
  formContainer: {
    width: "80%",
    marginLeft: 88,
    // alignItems: "center",
    // justifyContent: "center",
    paddingTop: 10
  },
  inputContainer: {
    width: "70%",
    marginBottom: 10
  },
  inputText: {
    borderBottomWidth: 2,
    borderBottomColor: "#d81b60",
    paddingVertical: 10,
    color: "#212121",
    fontSize: 30,
    fontWeight: "bold"
  },
  btn: {
    backgroundColor: "#d81b60",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
    marginTop: 20,
    fontWeight: "bold"
  },
  btnTxt: {
    color: "#FFF"
  },
  bgContainer: {
    flex: 1,
    alignItems: "center"
  }
});
