import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Dimensions,
  ScrollView,
  Image,
  TouchableOpacity,
} from "react-native";

const images = [
  "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fstatic.onecms.io%2Fwp-content%2Fuploads%2Fsites%2F47%2F2021%2F06%2F14%2Fsiberian-husky-puppy-grass-146571433-2000.jpg",
  "https://topaz.vn/wp-content/uploads/2022/02/dia-chi-ban-cho-golden-o-ha-noi-1.jpg",
  "https://www.invert.vn/media/uploads/uploads/2022/8/08160244-meo-anh-long-ngan-2.jpg",
  "https://meonhapkhau.com/wp-content/uploads/2021/06/Meo-Munchkin-noi-mua-va-gia-ban-533x400.jpg",
];

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const Setting = ({ navigation }) => {
  const [ImgActive, setImgActive] = useState();
  onChange = (nativeEvent) => {
    if (nativeEvent) {
      const slide = Math.ceil(
        nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width
      );
      if (slide != ImgActive) {
        setImgActive(slide);
      }
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.wrap}>
        <ScrollView
          onScroll={({ nativeEvent }) => onChange(nativeEvent)}
          showsHorizontalScrollIndicator={false}
          paddingEnabled
          horizontal
          style={styles.wrap}
        >
          {images.map((e, index) => (
            <Image
              key={e}
              resizeMode="stretch"
              style={styles.wrap}
              source={{ uri: e }}
            />
          ))}
        </ScrollView>
        <View style={styles.wrapDot}>
          {images.map((e, index) => {
            <Text
              key={e}
              style={ImgActive == index ? styles.dotActive : styles.dot}
            >
              ‚óè
            </Text>;
          })}
        </View>
      </View>
      <Text style={styles.textSetting}>Setting detail</Text>
      <View style={{justifyContent: 'center',alignItems: 'center'}}>
        <TouchableOpacity style={styles.btnLogout} onPress={() => navigation.popToTop()}>
          <Text style={styles.textLogout}>Log out</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

export default Setting;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#bdbdbd",
  },
  wrap: {
    width: WIDTH,
    height: HEIGHT * 0.25,
  },
  wrapDot: {
    position: "absolute",
    bottom: 0,
    flexDirection: "row",
    alignSelf: "center",
  },
  dotActive: {
    margin: 3,
    color: "black",
  },
  dot: {
    margin: 3,
    color: "white",
  },
  textSetting: {
    fontSize: 30,
    textAlign: "center",
  },
  btnLogout: {
    backgroundColor: "#ef5350",
    borderRadius: 5,
    width: 100,
    height: 30,
    marginLeft: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  textLogout: {
    fontSize: 20,
    color:"#f5f5f5"
  },
});
