import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Dimensions,
  ScrollView,
  Image,
  TouchableOpacity,
} from "react-native";



const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const Profile = ({ navigation }) => {


  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.wrap}>
        <ScrollView
      
          showsHorizontalScrollIndicator={false}
          paddingEnabled
          horizontal
          style={styles.wrap}
        >
          {images.map((e, index) => (
            <Image
              key={e}
              resizeMode="stretch"
              style={styles.wrap}
              source={{ uri: e }}
            />
          ))}
        </ScrollView>
        <View style={styles.wrapDot}>
          {images.map((e, index) => {
            <Text
              key={e}
              style={ImgActive == index ? styles.dotActive : styles.dot}
            >
              ‚óè
            </Text>;
          })}
        </View>
      </View>
      <Text style={styles.textSetting}>Setting detail</Text>
      <View style={{justifyContent: 'center',alignItems: 'center'}}>
        <TouchableOpacity style={styles.btnLogout} onPress={() => navigation.popToTop()}>
          <Text style={styles.textLogout}>Log out</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

export default Profile;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#bdbdbd",
  },
  wrap: {
    width: WIDTH,
    height: HEIGHT * 0.25,
  },
  wrapDot: {
    position: "absolute",
    bottom: 0,
    flexDirection: "row",
    alignSelf: "center",
  },
  dotActive: {
    margin: 3,
    color: "black",
  },
  dot: {
    margin: 3,
    color: "white",
  },
  textSetting: {
    fontSize: 30,
    textAlign: "center",
  },
  btnLogout: {
    backgroundColor: "#ef5350",
    borderRadius: 5,
    width: 100,
    height: 30,
    marginLeft: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  textLogout: {
    fontSize: 20,
    color:"#f5f5f5"
  },
});
