import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  ScrollView,
  SectionList,
  Modal,
  Pressable,
  Button,
} from "react-native";
// import { Dropdown, MultiSelect } from "react-native-element-dropdown";
import { Ionicons } from "@expo/vector-icons";
import ReadMore from "@fawazahmed/react-native-read-more";
import { fetchAllPet } from "../redux/actions/petActions";
import { useDispatch, useSelector } from "react-redux";

const items = [
  {
    id: 1,
    name: "Chó",
    data: [
      {
        id: 1,
        name: "Husky",
        img: "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fstatic.onecms.io%2Fwp-content%2Fuploads%2Fsites%2F47%2F2021%2F06%2F14%2Fsiberian-husky-puppy-grass-146571433-2000.jpg",
        price: "69 đồng",
        des: "Siberia",
      },
      {
        id: 2,
        name: "Corgi",
        img: "https://www.thukieng.com/wp-content/uploads/2018/09/giong-cho-corgi-chan-ngan-3.jpg",
        price: "70 đồng",
        des: "Wales",
      },
      {
        id: 3,
        name: "Golden",
        img: "https://topaz.vn/wp-content/uploads/2022/02/dia-chi-ban-cho-golden-o-ha-noi-1.jpg",
        price: "71 đồng",
        des: "Anh",
      },
      {
        id: 4,
        name: "Samoyed",
        img: "https://cdn.tgdd.vn/Files/2021/04/16/1343831/tim-hieu-giong-cho-samoyed-nguon-goc-dac-diem-cach-nuoi-bang-gia-202203291549425717.jpg",
        price: "72 đồng",
        des: "Nga",
      },
    ],
  },
  {
    id: 2,
    name: "Mèo",
    data: [
      {
        id: 5,
        name: "Mèo anh lông ngắn",
        img: "https://www.invert.vn/media/uploads/uploads/2022/8/08160244-meo-anh-long-ngan-2.jpg",
        price: "69 đồng",
        des: "Anh",
      },
      {
        id: 6,
        name: "Mèo tai cụp",
        img: "https://zoipet.com/wp-content/uploads/2020/03/meo-scottish.jpg",
        price: "70 đồng",
        des: "Anh",
      },
      {
        id: 7,
        name: "Mèo anh lông dài ",
        img: "https://tindep.com/wp-content/uploads/2018/04/huong-dan-cham-soc-meo-Anh-long-dai-1.jpg",
        price: "71 đồng",
        des: "Anh",
      },
      {
        id: 8,
        name: "Mèo Munchkin",
        img: "https://meonhapkhau.com/wp-content/uploads/2021/06/Meo-Munchkin-noi-mua-va-gia-ban-533x400.jpg",
        price: "72 đồng",
        des: "USA",
      },
    ],
  },
];

const ListItemView = ({ props }) => {
  const dispatch = useDispatch();
  const db = useSelector((store) => store.pets);
  console.log("db", db.pets);
  const [data, setData] = useState([]);
  useEffect(() => {
    dispatch(fetchAllCourses());
    setData(db.pets);
    console.log("Hello", data);
  }, []);
  return (
    <View style={styles.ListItemContainer}>
      <SectionList
        ListHeaderComponent={HeaderComponent}
        ItemSeparatorComponent={ItemSeparator}
        renderSectionHeader={({ section }) => (
          <Text style={styles.sectionHeader}>
            Loại thú cưng: {section.name}
          </Text>
        )}
        sections={items}
        renderItem={ItemComponent}
      />
    </View>
  );
};

const ItemSeparator = ({ params }) => {
  return <View style={styles.separator}></View>;
};

const HeaderComponent = () => {
  return (
    <View style={styles.headerListComponent}>
      <Text style={styles.headerTitle}>Danh sách thú cưng</Text>
    </View>
  );
};

const ItemComponent = ({ item, navigation }) => {
  const [ModalVisible, setModalVisible] = useState(false);
  return (
    <ScrollView style={{ marginLeft: 6 }}>
      <View style={styles.select}>
        <View style={styles.itemContainer}>
          <View style={styles.itemInfo}>
            <Image source={{ uri: item.img }} style={styles.imgItem} />
          </View>
          <View style={styles.itemDetail}>
            <Text style={styles.nameText}>Tên: {item.name}</Text>
            <Text style={styles.priceText}>Giá: {item.price}</Text>
            <Text style={styles.desText}>Xuất sứ: {item.des}</Text>
            <View style={{ flexDirection: "row" }}>
              <TouchableOpacity
                style={{ flexDirection: "row" }}
                onPress={() => {
                  setModalVisible(true);
                }}
              >
                <Ionicons name="search" size={20} color="black" />
                <Text>View detail</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{ flexDirection: "row", marginLeft: 10 }}
                onPress={() => {
                  navigation.navigate("Cart");
                }}
              >
                <Ionicons name="cart" size={20} color="black" />
                <Text>Add to cart</Text>
              </TouchableOpacity>
            </View>
            <View style={styles.centeredView}>
              <Modal
                animationType="slide"
                transparent={true}
                visible={ModalVisible}
                onRequestClose={() => setModalVisible(false)}
              >
                <Pressable
                  style={styles.outsideModal}
                  onPress={(event) => {
                    if (event.target == event.currentTarget) {
                      setModalVisible(false);
                    }
                  }}
                >
                  <View style={styles.modal}>
                    <View style={styles.imageContainer}>
                      <View style={styles.image}>
                        <Image
                          style={styles.imgInfo}
                          source={require("../assets/meo.jpg")}
                        />
                      </View>
                      <View style={styles.text}>
                        <Text style={styles.textName}>Mèo Munchkin</Text>
                        <Text style={styles.textPrice}>149.400 đ</Text>

                        {/* <TouchableOpacity
                          title="Add to cart"
                          onPress={() => navigation.navigate("Cart")}
                        ></TouchableOpacity> */}
                      </View>
                    </View>
                    <Text style={styles.text4}>Thông tin chi tiết</Text>
                    <View style={styles.description}>
                      <View>
                        <Text style={styles.textTitle}>Mã hàng : </Text>
                        <Text style={styles.textTitle}>Giống loài: </Text>
                        <Text style={styles.textTitle}>Tình trạng: </Text>
                        <Text style={styles.textTitle}>Nguồn gốc : </Text>
                      </View>
                      <View>
                        <Text style={styles.text3}>8154156515461</Text>
                        <Text style={styles.text3}>Mèo</Text>
                        <Text style={styles.text3}>Trưởng thành</Text>
                        <Text style={styles.text3}>USA</Text>
                      </View>
                    </View>
                    <Text style={styles.textTitle}>Mô tả:</Text>
                    <ReadMore numberOfLines={3} style={styles.textDes}>
                      Không chỉ có một vẻ ngoài đáng yêu và ưa nhìn, những chú
                      mèo Munchkin cũng có tính cách rất dễ chịu và thân thiện.
                      Những chú mèo Munchkin rất tinh nghịch, lém lỉnh và vui
                      tươi như vẻ ngoài của chúng. Tuy nhiên, chúng cũng rất
                      ngọt ngào, đặc biệt là rất quấn chủ. Ngoài ra, những chú
                      mèo Munchkin cũng rất thông minh, hiền lành và dễ tính.
                      Chúng rất hòa đồng và dễ gần với trẻ con. Những gia đình
                      có trẻ nhỏ thì nên nuôi một chú mèo Munchkin trong nhà để
                      trẻ nhỏ được bầu bạn với thú cưng
                    </ReadMore>
                    <TouchableOpacity
                      style={styles.btnCart}
                      onPress={() => {
                        navigation.navigate("Cart", { petid: id });
                      }}
                    >
                      <Text style={styles.btnText1}>Add to cart</Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                      style={styles.btnClosingModal}
                      onPress={() => setModalVisible(false)}
                    >
                      <Text style={styles.btnText2}>Back</Text>
                    </TouchableOpacity>
                  </View>
                </Pressable>
              </Modal>
            </View>
          </View>
        </View>
      </View>
    </ScrollView>
  );
};

