import React from "react";
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  Image,
  TouchableOpacity,
} from "react-native";

const items = [
  {
    id: 1,
    name: "Táo",
    img: "https://kenh14cdn.com/203336854389633024/2022/8/4/ai-hinh-khong-he-gia-tran-cua-my-nhan-4000-nam-mat-thon-vai-gay-chan-dai-deu-la-don-1-16044151377011682443371-1607529849-689-width660height1004-1659598005458348527112.jpg",
    price: "69 đồng",
    des: "ngon",
  },
  {
    id: 2,
    name: "Lê",
    img: "https://icdn.dantri.com.vn/thumb_w/680/2022/08/12/dich-le-nhiet-ba-2-1660267454447.jpg",
    price: "70 đồng",
    des: "mlem",
  },
  {
    id: 3,
    name: "Chuối",
    img: "https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/11/15/974022/Duong-Mich4.jpg",
    price: "71 đồng",
    des: "chẹp chẹp",
  },
  {
    id: 4,
    name: "Coca",
    img: "https://image.thanhnien.vn/w1024/Uploaded/2022/zxaijr/2022_03_30/trieuledinhphunhantronthue1-4371.jpeg",
    price: "72 đồng",
    des: "yummy",
  },
];

const ListItemView = ({ params }) => {
  return (
    <View style={styles.ListItemContainer}>
      <FlatList
        data={items}
        renderItem={ItemComponent}
        ListHeaderComponent={HeaderComponent}
      ></FlatList>
    </View>
  );
};

const HeaderComponent = () => {
  return (
    <View style={styles.headerListComponent}>
      <Text style={styles.headerTitle}>List Fruits</Text>
    </View>
  );
};

const ItemComponent = ({ item }) => {
  return (
    <TouchableOpacity style={styles.select}>
      <View style={styles.itemContainer}>
        <View style={styles.itemInfo}>
          <Image source={{ uri: item.img }} style={styles.imgItem} />
        </View>
        <View style={styles.itemDetail}>
          <Text style={styles.nameText}>Tên: {item.name}</Text>
          <Text style={styles.priceText}>Giá: {item.price}</Text>
          <Text style={styles.desText}>Mô tả: {item.des}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );
};

export default ListItemView;
const styles = StyleSheet.create({
  ListItemContainer: {
    flex: 1,
  },
  itemContainer: {
    flexDirection: "row",
    //marginLeft: 15,
  },
  select: {
    borderWidth: 2,
    borderColor: "#1b1b1b",
    borderRadius: 4,
    marginTop: 15,
    width: 360,
    height: 104,
    marginLeft: 5,
  },
  imgItem: {
    width: 100,
    height: 100,
  },
  itemDetail: {
    marginLeft: 15,
    marginBottom: 15,
  },
  nameText: {
    // marginLeft: -5,
    color: "#424242",
    fontSize: 22,
  },
  priceText: {
    color: "#9f0000",
    fontSize: 22,
  },
  desText: {
    color: "#263238",
    fontSize: 22,
  },
  headerListComponent: {
    color: "#4a148c",
    alignItems: "center",
  },
  headerTitle: {
    marginTop: 50,
    marginBottom: 20,
    fontSize: 40,
  },
});
