export const CREATE_PET = "CREATE_PET";
export const UPDATE_PET = "UPDATE_PET";
export const DELETE_PET = "DELETE_PET";
export const GETALL_PET = "GETALL_PET";
//action creator
export const createPet = (pet) => {
  return {
    type: "CREATE_PET",
    payload: {
      petId: pet.id,
      petCategory: pet.category,
      petName: pet.name,
      petAge: pet.age,
      petGender: pet.gender,
      petPrice: pet.price,
      petCondition: pet.condition,
      petImage: pet.image,
    },
  };
};

export const updatePet = (pet) => {
  return {
    type: "UPDATE_PET",
    payload: {
      petId: pet.id,
      petCategory: pet.category,
      petName: pet.name,
      petAge: pet.age,
      petGender: pet.gender,
      petPrice: pet.price,
      petCondition: pet.condition,
      petImage: pet.image,
    },
  };
};

export const deletePet = (id, category) => {
  return {
    type: "DELETE_PET",
    payload: {
      petId: id,
      petCategory: category,
    },
  };
};

export const getAllPet = (Shop) => {
  return {
    type: "GETALL_CATEGORY",
    payload: Shop,
  };
};

export const fetchAllPet = () => {
  return (dispatch) => {
    const getData = async () => {
      try {
        const response = await fetch("http://localhost:5555/api/Shop");
        const pet = await response.json();
        console.log("here", pet);
        //dispatch action to reducer
        dispatch(getAllPet(pet));
      } catch (err) {
        console.error(err);
      }
    };
    getData();
  };
};
