import { CREATE_PET, UPDATE_PET, DELETE_PET } from "../actions/petActions";

const initialState = {
  pets: [],
  currentPet: {},
};

export const petReducer = (state = initialState, action) => {
  switch (action.type) {
    case CREATE_PET:
      let newPet = {
        id: action.payload.petId,
        category: action.payload.petCategory,
        name: action.payload.petName,
        age: action.payload.petAge,
        gender: action.payload.petGender,
        price: action.payload.petPrice,
        condition: action.payload.petCondition,
        image: action.payload.petImage,
      };
      return {
        ...state,
        pets: [...state.pets, newPet],
        currentPet: newPet,
      };
    case UPDATE_PET:
      return {
        ...state,
        pets: state.pets.map((x) => {
          if (x.id === action.payload.petId) {
            return {
              ...x,
              category: action.payload.petCategory,
              name: action.payload.petName,
              age: action.payload.petAge,
              gender: action.payload.petGender,
              price: action.payload.petPrice,
              condition: action.payload.petCondition,
              image: action.payload.petImage,
            };
          }
          return x;
        }),
      };
    case DELETE_PET:
      return {
        ...state,
        pets: state.pets.filter((x) => x.id !== action.payload.petId),
      };
    default:
      return { ...state };
  }
};
