import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
  Image,
  Modal,
  Pressable,
} from "react-native";

import { Ionicons, AntDesign } from "@expo/vector-icons";
//step1
import { useDispatch, useSelector } from "react-redux";

const ViewAllItem = ({ navigation }) => {
  //const dispatch = useDispatch();
  const db = useSelector((state) => state.items);
  //console.log("db", db.categories);
  //console.log(db);
  //   const [data, setData] = useState([]);
  //   useEffect(() => {
  //     dispatch(fetchAllCategory());
  //     setData(db.categories);
  //     //console.log("Hello", data);
  //   }, []);
  return (
    <View style={styles.CRUDContainer}>
      <View>
        <Text style={styles.tiltle}>LIST PET</Text>
      </View>
      <View style={styles.ProductAllContainer}>
        {db.pets ? (
          db.pets.map((item) => {
            return (
              <View key={item.id} style={styles.tagContainer}>
                <View style={styles.contentMain}>
                  <Image source={item.image} style={styles.img} />
                  <View style={styles.View}>
                    <Text style={styles.textName}>Tên: {item.name}</Text>
                    <Text style={styles.textPrice}>Giá: {item.price} VNĐ</Text>
                    <View>
                      <TouchableOpacity
                        style={styles.btnUpdate}
                        onPress={() =>
                          navigation.navigate("EditItem", {
                            itemId: item.id,
                            itemName: item.name,
                            itemQuantity: item.quantity,
                            itemPrice: item.price,
                            itemDes: item.des,
                            itemImage: item.image,
                          })
                        }
                      >
                        <AntDesign name="edit" size={24} color="black" />
                        <Text style={styles.txtUpdate}>Update</Text>
                      </TouchableOpacity>
                    </View>
                    <View style={{ marginTop: 10 }}>
                      <TouchableOpacity
                        style={styles.btnView}
                        onPress={() => {
                          setModalVisible(true);
                        }}
                      >
                        <Ionicons name="search" size={24} color="black" />
                        <Text style={styles.txtView}>View Detail</Text>
                      </TouchableOpacity>
                    </View>
                    <View style={styles.centeredView}>
                      <Modal
                        animationType="slide"
                        transparent={true}
                        visible={ModalVisible}
                        onRequestClose={() => setModalVisible(false)}
                      >
                        <Pressable
                          style={styles.outsideModal}
                          onPress={(event) => {
                            if (event.target == event.currentTarget) {
                              setModalVisible(false);
                            }
                          }}
                        >
                          <View style={styles.modal}>
                            <View style={styles.imageContainer}>
                              <View style={styles.image}>
                                <Image
                                  style={styles.imgInfo}
                                  source={item.image}
                                />
                              </View>
                              <View style={styles.text}>
                                <Text style={styles.txtName}>{item.name}</Text>
                                <Text style={styles.txtPrice}>
                                  {item.price} VNĐ
                                </Text>

                                <TouchableOpacity
                                  title="Add to cart"
                                  onPress={() => navigation.navigate("Cart")}
                                ></TouchableOpacity>
                              </View>
                            </View>
                            <Text style={styles.text4}>Thông tin chi tiết</Text>
                            <View style={styles.description}>
                              <View>
                                <Text style={styles.textTitle}>ID:</Text>
                                <Text style={styles.textTitle}>Quantity:</Text>
                                <Text style={styles.textTitle}>
                                  Description:
                                </Text>
                              </View>
                              <View>
                                <Text style={styles.text3}>{item.id}</Text>
                                <Text style={styles.text3}>
                                  {item.quantity}
                                </Text>
                                <Text style={styles.text3}>{item.des}</Text>
                              </View>
                            </View>
                            <TouchableOpacity
                              style={styles.btnClosingModal}
                              onPress={() => setModalVisible(false)}
                            >
                              <Text style={styles.btnText}>Back</Text>
                            </TouchableOpacity>
                          </View>
                        </Pressable>
                      </Modal>
                    </View>
                  </View>
                </View>
              </View>
            );
          })
        ) : (
          <View>
            <Text>No data</Text>
          </View>
        )}
      </View>
      <View style={{ alignItems: "center", justifyContent: "center" }}>
        <TouchableOpacity
          style={styles.btn}
          onPress={() => {
            navigation.navigate("CreateItem");
          }}
        >
          <Ionicons style={styles.notiBtn} name="add" color="#fff" size={24} />
        </TouchableOpacity>
      </View>
    </View>
  );
};
export default ViewAllItem;
const styles = StyleSheet.create({
  CRUDContainer: {
    flex: 1,
    alignItems: "center",
    marginTop: 4,
  },
  bgContainer: {
    height: "auto",
  },
  contentMain: {
    flexDirection: "row",
  },
  View: {
    marginLeft: 24,
    marginBottom: 28,
    justifyContent: "center",
  },
  ProductAllContainer: {
    width: "100%",
    //flexDirection: "row",
    // alignItems: "center",
    //flexWrap: "wrap",
  },
  tagContainer: {
    alignItems: "flex-start",
    justifyContent: "center",
    width: "80%",
    height: 160,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#d81b60",
    paddingLeft: 10,
    margin: 10,
  },
  tagCT: {
    width: "auto",
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    padding: 10,
    margin: 10,
    borderRadius: 25,
    borderWidth: 1,
    borderColor: "#fff",
    backgroundColor: "#fff",
  },
  tagText: {
    color: "#d81b60",
    fontSize: 35,
  },
  tagPText: {
    color: "#fff",
  },
  btn: {
    backgroundColor: "#d81b60",
    width: 45,
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 45 / 2,
  },
  img: {
    width: 120,
    height: 120,
    borderRadius: 20,
    marginTop: 6,
  },
  tittle: {
    paddingTop: 20,
    fontSize: 30,
    fontWeight: "bold",
    color: "red",
  },
  // tagss: {
  //   fontSize: 10,
  //   color: "#000",
  // },
  tagAllContainer: {
    flexDirection: "row",
    alignSelf: "flex-start",
  },
  btnUpdate: {
    backgroundColor: "#ef9a9a",
    borderRadius: 5,
    flexDirection: "row",
  },
  btnView: {
    backgroundColor: "#b39ddb",
    borderRadius: 5,
    flexDirection: "row",
  },
  txtUpdate: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
  txtView: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 18,
    marginLeft: 6,
  },
});
