import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Dimensions,
  ScrollView,
  Image,
} from "react-native";

const images = [
  "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fstatic.onecms.io%2Fwp-content%2Fuploads%2Fsites%2F47%2F2021%2F06%2F14%2Fsiberian-husky-puppy-grass-146571433-2000.jpg",
  "https://topaz.vn/wp-content/uploads/2022/02/dia-chi-ban-cho-golden-o-ha-noi-1.jpg",
  "https://www.invert.vn/media/uploads/uploads/2022/8/08160244-meo-anh-long-ngan-2.jpg",
  "https://meonhapkhau.com/wp-content/uploads/2021/06/Meo-Munchkin-noi-mua-va-gia-ban-533x400.jpg",
];

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const Home = ({ params }) => {
  const [ImgActive, setImgActive] = useState();
  onChange = (nativeEvent) => {
    if (nativeEvent) {
      const slide = Math.ceil(
        nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width
      );
      if (slide != ImgActive) {
        setImgActive(slide);
      }
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View>
        <Image
          source={require("../assets/bghome.jpg")}
          style={{ width: 400, height: 200 }}
          resizeMode="stretch"
        />
      </View>
      <Text style={styles.header}>Những mặt hàng bán chạy trong năm</Text>
      <View style={styles.wrap}>
        <ScrollView
          onScroll={({ nativeEvent }) => onChange(nativeEvent)}
          showsHorizontalScrollIndicator={false}
          paddingEnabled
          horizontal
          style={styles.wrap}
        >
          {images.map((e, index) => (
            <Image
              key={e}
              resizeMode="stretch"
              style={styles.wrap}
              source={{ uri: e }}
            />
          ))}
        </ScrollView>
        <View style={styles.wrapDot}>
          {images.map((e, index) => {
            <Text
              key={e}
              style={ImgActive == index ? styles.dotActive : styles.dot}
            >
              ●
            </Text>;
          })}
        </View>
      </View>
      <View>
        <Text style={styles.header}>About PetShop</Text>
        <Text style={styles.txt}>
          PetShop là thương hiệu đầu tiên tại Việt Nam xây dựng thành công hệ
          sinh thái khép kín gồm các Trang trại nhập khẩu, nhân giống chó mèo
          cảnh, chuỗi siêu thị thú cưng, Phòng khám thú y, dịch vụ Spa &
          Grooming tại Tphcm, Hà Nội & Đà Lạt
        </Text>
      </View>
      <View>
        <Text style={styles.header}>Các chi nhánh của Shop</Text>
        <Text style={{marginLeft:6}}>
          PetShop - Ba Tháng Hai: 606/121 đường 3 tháng 2, phường 14, quận 10,
          thành phố Hồ Chí Minh
        </Text>
        <Text style={{marginLeft:6}}>
          PetShop - Cộng Hòa: 391 đường Cộng Hòa, phường 13, quận Tân Bình,
          Tphcm.
        </Text>
        <Text> 
          PetShop - Tây Hồ: 81 đường Nghi Tàm, phường Yên Phụ, quận Tây Hồ, Hà
          Nội
        </Text>
      </View>
      <View>
        <Text style={styles.header}>Liên hệ</Text>
        <Text style={{ textAlign: "center", marginRight: 42 }}>
          Hotline: 0965086079
        </Text>
        <Text style={{ textAlign: "center" }}>Website: https://petshop.vn</Text>
        <Text style={{ textAlign: "center", marginLeft: 20 }}>
          Email: petshop.vn@gmail.com
        </Text>
      </View>
      <View>
        <Text style={styles.header}>Chính sách mua hàng</Text>
        <Text style={{ textAlign: "center" }}>Mua Trả Góp 0%</Text>
        <Text style={{ textAlign: "center", marginLeft: 10 }}>
          Bảo Hành, Đổi Trả
        </Text>
        <Text style={{ textAlign: "center", marginLeft: 24 }}>
          Chính Sách Bảo Mật
        </Text>
      </View>
    </ScrollView>
  );
};

export default Home;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#bdbdbd",
  },
  header: {
    fontSize: 22,
    textAlign: "center",
    fontWeight: "bold",
  },
  wrap: {
    width: WIDTH,
    height: HEIGHT * 0.25,
  },
  wrapDot: {
    position: "absolute",
    bottom: 0,
    flexDirection: "row",
    alignSelf: "center",
  },
  dotActive: {
    margin: 3,
    color: "black",
  },
  dot: {
    margin: 3,
    color: "white",
  },
  txt: {
    alignSelf: "center",
    flexWrap: "wrap",
  },
});
