import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  SafeAreaView,
  Image,
} from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import { DELETE_TAG } from "../Redux/Actions/TagAction";

import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { updateTag, deleteTag } from "../Redux/Actions/TagAction";

const EditTag = ({ navigation, route }) => {
  const { tagId } = route.params;
  const { tagName } = route.params;
  const { tagIcon } = route.params;

  const [id, setId] = useState(tagId);
  const [name, setName] = useState("");
  const [icon, setIcon] = useState("");

  const dispatch = useDispatch();
  const db = useSelector((state) => state.tags);

  const handleDeleteTag = (id) => {
    dispatch(deleteTag(id));
    navigation.navigate("ViewAll");
  };

  const handleUpdateTag = () => {
    let newT = {
      id: id,
      name: name,
      icon: icon,
    };
    dispatch(updateTag(newT));
    navigation.navigate("ViewAll");
  };

  return (
    <View style={styles.LoginContainer}>
      <View style={styles.inputContainer}>
        <TextInput value={tagId} style={styles.inputText} />

        <TextInput
          placeholder={tagName}
          style={styles.inputText}
          value={name}
          onChangeText={(val) => {
            setName(val);
          }}
        />
        <TextInput
          placeholder={tagIcon}
          style={styles.inputText}
          value={icon}
          onChangeText={(val) => {
            setIcon(val);
          }}
        />
      </View>

      <TouchableOpacity style={styles.btn} onPress={() => handleUpdateTag()}>
        <Text style={styles.btnText}>Update</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.btn}
        onPress={() => handleDeleteTag(tagId)}
      >
        <Text style={styles.btnText}>Delete</Text>
      </TouchableOpacity>
    </View>
  );
};
export default EditTag;
const styles = StyleSheet.create({
  LoginContainer: {
    flex: 1,
  },
  bgContainer: {
    flex: 1,
    alignItems: "center",
    paddingVertical: 20,
  },
  LoginLogo: {
    marginTop: 10,
    width: 80,
    height: 80,
    backgroundColor: "#64b5f6",
    alignItems: "center",
    borderRadius: 40,
    justifyContent: "center",
  },
  LogoImg: {
    width: "100%",
    height: "100%",
    borderRadius: 40,
  },

  formContainer: {
    width: "80%",
  },
  inputContainer: {
    // width:'90%',
    alignSelf: "center",
    flexDirection: "column",
    justifyContent: "center",
    width: "80%",
  },
  inputText: {
    color: "black",
    borderWidth: 1,
    borderColor: "#2286c3",
    width: "100%",
    paddingVertical: 15,
    marginVertical: 15,
    paddingLeft: 5,
    opacity: 0.8,
  },
  btn: {
    backgroundColor: "white",
    width: "100%",
    height: 55,
    alignItems: "center",
    alignSelf: "center",
    justifyContent: "center",
    borderRadius: 10,
    borderWidth: 3,
    borderColor: "#fffde7",
    marginTop: 30,
  },
  btnText: {
    color: "#2286c3",
    fontWeight: "bold",
    fontSize: 25,
  },
  LoginNotice: {
    width: "100%",
    flexDirection: "row",
    alignSelf: "center",
    paddingTop: 5,
    justifyContent: "center",
  },
  notiBtn: {
    paddingRight: "80%",
  },
  nationFlag: {
    width: 30,
    height: 20,
  },
  flagImg: {
    width: "100%",
    height: "100%",
  },
  otherContent: {
    flexDirection: "row",
    width: "100%",
  },
  forgetPW: {
    color: "#c3fdff",
    fontSize: 12,
    paddingRight: "35%",
  },
  signUp: {
    color: "#c3fdff",
    fontSize: 12,
  },
  inputIcon: {
    marginRight: 5,
    backgroundColor: "#0077c2",
    height: 40,
    width: 40,
    textAlign: "center",
    justifyContent: "center",
    opacity: 0.8,
  },
  fingerPrint: {
    alignSelf: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  HelpView: {
    height: "20%",
    width: "100%",
    alignItems: "center",
    justifyContent: "flex-end",
  },
  help: {
    fontSize: 18,
    color: "#1a237e",
    fontWeight: "600",
  },
});
