import React from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
  TextInput,
  TouchableOpacity,
} from "react-native";
//import { FontAwesome, Ionicons, FontAwesome5 } from "@expo/vector-icons";
import Icon from "react-native-vector-icons/FontAwesome";

const Login = ({ navigation }) => {
  return (
    <View style={styles.loginContainer}>
      <Image
        style={{ width: "100%", height: "40%", alignItems: "center" }}
        source={require("../assets/hsu.jpg")}
        resizeMode="cover"
      ></Image>

      <Text style={styles.title}>Hệ thống tài khoản HSU</Text>
      <View style={styles.formContainer}>
        <View style={styles.iconUser}>
          <Icon name="user" size={38} color="#2979ff" />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            placeholder="Username"
            placeholderTextColor="#9e9e9e"
            style={styles.inputText}
          />
        </View>
      </View>
      <View style={styles.formContainer}>
        <View style={styles.iconUser}>
          <Icon name="lock" color="#2979ff" size={38} />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            placeholder="Password"
            placeholderTextColor="#9e9e9e"
            style={styles.inputText}
            secureTextEntry={true}
          />
        </View>
      </View>
      <View style={styles.submit}>
        <TouchableOpacity
          style={styles.btn}
          onPress={() => navigation.navigate("FloatList")}
        >
          <Text style={styles.btnText}>Đăng nhập</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.bottom}>
        <TouchableOpacity>
          <Text style={styles.botText}>Cần hỗ trợ ?</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default Login;
const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
  },
  //   bgContainer: {
  //     flex: 1,
  //     height: "30%",
  //   },
  title: {
    fontSize: 30,
    fontWeight: "bold",
    color: "#2979ff",
    textAlign: "center",
    //marginTop: 250,
  },
  formContainer: {
    width: "100%",
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginTop: 10,
  },
  inputContainer: {
    width: "50%",
    marginLeft: 5,
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "#2979ff",
  },
  inputText: {
    marginLeft: 10,
  },
  btn: {
    backgroundColor: "#2979ff",
    width: "70%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
  },
  btnText: {
    fontSize: 20,
    color: "#fff",
  },
  submit: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 10,
  },
  bottom: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 60,
  },
  botText: {
    fontSize: 20,
    color: "#2979ff",
  },
});
